catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
    g_form.clearOptions('e_voucher_amount');
    if (newValue == 'E-Commerce') {
        g_form.addOption('e_voucher_amount', '200', 'Rs.200', 10);
        g_form.addOption('e_voucher_amount', '250', 'Rs.250', 20);
        g_form.addOption('e_voucher_amount', '500 ', 'Rs.500', 30);
		g_form.addOption('e_voucher_amount', '1000 ', 'Rs.1000', 30);
	}
    else if (newValue == 'Shopping') {
        g_form.addOption('e_voucher_amount', '400', 'Rs.400', 10);
        g_form.addOption('e_voucher_amount', '750', 'Rs.750', 20);
		}
    else if (newValue == 'Food Delivery') {
        g_form.addOption('e_voucher_amount', '100 ', 'Rs.100 ', 10);
        g_form.addOption('e_voucher_amount', '200 ', 'Rs.200 ', 20);
		g_form.addOption('e_voucher_amount', '400 ', 'Rs.400', 30);
			}
    else if (newValue == 'Sports Wear'){
        g_form.addOption('e_voucher_amount', '250 ', 'Rs.250 ', 10);
		g_form.addOption('e_voucher_amount', '500 ', 'Rs.500 ', 20);
		g_form.addOption('e_voucher_amount', '750  ', 'Rs.750 ', 30);
                }
			}
