catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    g_form.clearOptions('e_voucher_brand');
    if (newValue == 'E-Commerce') {
        g_form.addOption('e_voucher_brand', 'amazon', 'Amazon', 10);
        g_form.addOption('e_voucher_brand', 'flipkart', 'Flipkart', 20);
        g_form.addOption('e_voucher_brand', 'Myntra ', 'Myntra', 30);
	}
    else if (newValue == 'Shopping') {
        g_form.addOption('e_voucher_brand', 'lifestyle ', 'Lifestyle', 10);
        g_form.addOption('e_voucher_brand', 'allen Solly', 'Allen Solly', 20);
		}
    else if (newValue == 'Food Delivery') {
        g_form.addOption('e_voucher_brand', 'zomato ', 'Zomato ', 10);
        g_form.addOption('e_voucher_brand', 'swiggy ', 'Swiggy ', 20);
			}
    else if (newValue == 'Sports Wear'){
        g_form.addOption('e_voucher_brand', 'puma ', 'Puma ', 10);
		g_form.addOption('e_voucher_brand', 'adidas ', 'Adidas ', 20);
		g_form.addOption('e_voucher_brand', 'nike  ', 'Nike ', 30);
                }
			}
		      //Type appropriate comment here, and begin script below
